package com.imooc.flow1;

public class PlusDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		int sum=0;
		while(i<=5) {
			sum+=i;			
			i++;	
			
		}
		System.out.println("sum="+sum);
		int j=1;
		int sum_j=0;
		while(j<=15) {
			sum_j+=j;			
			j=j+2;			
		}
		System.out.println("sum_j="+sum_j);
	
		

	}

}
